package ex;

public class ConditionalOperator_ex {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		if(true && false) {
			System.out.println("no");
		}
		if(true && true) {
			System.out.println("yes");
		}
		if(true || false) {
			System.out.println("yes");
		}
	}

}
